    setMode("human");
    setSteps(0);
    setTechStats(null);
    startTimer();
  };
  const handlePlayAI = async () => {
    if (!board || mode === "ai" || mode === "solved") return;
    stopAll();
    setMode("ai");
    setTechStats(null);
    setLoading(true);
    try {
      const res = await fetch(`${API}/solve`, {
        method: "POST",
        headers: {
          "Content-Type": "application/json"
        },
        body: JSON.stringify({
          board
        })
      });
      const data = await res.json();
      if (data.error) {
        setError(data.error);
        setMode("idle");
        setLoading(false);
        return;
      }
      if (data.already_solved) {
        setMode("solved");
        setShowVictory(true);
        setLoading(false);
        return;
      }

      // Store observability metrics
      setTechStats({
        solution_depth: data.solution_depth,
        nodes_explored: data.nodes_explored,
        time_taken_ms: data.time_taken_ms,
        branching_factor: data.branching_factor,
        heuristic_start: data.heuristic_start
      });
      setAiPath(data.solution_path);
      setAiIdx(0);
      startTimer();
      let i = 0;
      aiRef.current = setInterval(() => {
        if (i >= data.solution_path.length) {
          clearInterval(aiRef.current);
          clearInterval(timerRef.current);
          setMode("solved");
          setShowVictory(true);
          return;
        }
        const step = data.solution_path[i];
        setPrevBoard(cur => cur);
        setBoard(cur => {
          setPrevBoard(cur);
          return step.board;
        });
        setSteps(step.step);
        setAiIdx(i + 1);
        i++;
      }, 380);
    } catch {
      setError("âš ï¸ AI solver failed. Is Flask running?");
      setMode("idle");
    }
    setLoading(false);
  };
